import {useContext,React} from "react";
import { Navigate } from "react-router-dom";
import { UserContext } from "../components/UserContext";

const LoginPage = () => {
  const [username, setUsername] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [redirect, setRedirect] = React.useState(false);
    const {setUserInfo} = useContext(UserContext);

    async function login(ev) {
      ev.preventDefault();
      const response = await fetch('http://localhost:4000/login', {
        method: 'POST',
        body: JSON.stringify({username, password}),
        headers: {'Content-Type':'application/json'},
        credentials: 'include',
      });
      if (response.ok) {
        response.json().then(userInfo => {
          setUserInfo(userInfo);
          setRedirect(true);
        });
        setRedirect(true)
      } else {
        alert('wrong credentials');
      }
    }

    if (redirect) {
      return <Navigate to={'/'} />
    }

  return (
    <form action="" onSubmit={login} className="max-width-[400px]">
    <h1 className="text-center font-bold text-3xl my-10">Log In</h1>
    <input
      type="text"
      value={username}
      onChange={e=> setUsername(e.target.value)}
      className="bg-[#fff] w-full p-2 block mb-5 border-[#ddd] border-2 rounded-lg"
      placeholder="username"
    />
    <input
      type="password"
      value={password}
      onChange={e=> setPassword(e.target.value)}
      className="bg-[#fff] w-full p-2 block mb-5 border-[#ddd] border-2 rounded-lg"
      placeholder="password"
    />
    <button className="w-full block bg-[#555] text-[#fff] rounded-lg py-2">
      Login
    </button>
  </form>
  )
};

export default LoginPage;
